import React, {Component} from 'react';
import {Input, Dropdown, Menu} from 'antd';
import {browserHistory} from 'react-router';
import './Nav.css';
import axios from 'axios';

const {Search} = Input;

class NavLogin extends Component{
    constructor(props){
        super(props);

        this.state = {
            user:{
                username: window.localStorage.getItem("nickname"),
                avatar: 'data:image/png;base64,'
            }
        }

        this.postWrite = this.postWrite.bind(this);
        this.home = this.home.bind(this);
        this.menuLink = this.menuLink.bind(this);
        this.search = this.search.bind(this);
    }

    componentDidMount(){
        axios({
            method: 'GET',
            url: 'api/user-information-management/avatar/download',
            headers: {
                "ps-token": window.localStorage.getItem("token")
            }
        }).then((res)=>{
            const img = res.data;
            if(img.length !== 0){
                let user = this.state.user;
                user.avatar = img;
                this.setState({user: user});
            }
        })
    }

    home(){
        window.location.href = "/";
    }

    search(value, event){
        const history = browserHistory;
        history.push({pathname:"search",query: {value : value}});
    }

    postWrite(){
        window.location.href = "write";
    }

    menuLink(event){
        switch(event.key){
            case "1": window.location.href = "index";break;
            case "2": window.location.href = "set";break;
            case "3": {window.localStorage.removeItem("token"); window.localStorage.removeItem("nickname"); window.location.href='/'; break;}
            default: break;
        }
    }

    render(){
        const username = this.state.user.username;
        const avatar = this.state.user.avatar;
        const menuLink = this.menuLink;
        const menu = (
            <Menu onClick={menuLink}>
              <Menu.Item key="1" icon={<img alt="头像" src={avatar}></img>}>{username}</Menu.Item>
              <Menu.Item key="2" icon={<img alt="设置" src="./个人设置.png"></img>}>个人设置</Menu.Item>
              <Menu.Item key="3" icon={<img alt="退出" src="./退出登录.png"></img>}>退出</Menu.Item>
            </Menu>
          );
        const postWrite = this.postWrite;
        const home = this.home;
        const search = this.search;
        return(
            <div className={"nav"}>
                <div className={"nav-content"}>
                    <div className={"home"} onClick={home}><img alt="首页" className={"image"} src="./首页.png"/>首页</div>
                    <div className={"search"}><Search style={{marginTop: '16px'}} placeholder={'请输入搜索用户、帖子标题'} onSearch={search}></Search></div>
                    <div className={"login-register"} onClick={postWrite}><img alt="登录" className={"image"} src="./申请.png"/>发帖</div>
                    <div id={"user"} style={{float: 'right'}}>
                    <Dropdown overlay={menu}>
                        <a className="ant-dropdown-link" onClick={e => e.preventDefault()} style={{color: '#fff'}}>
                            <img alt="账户" className={"image"} src="./账户.png"/>
                            {username}
                        </a>
                    </Dropdown>
                    </div>
                    
                </div>
            </div>
        );
    }
}

export default NavLogin;