import React, {Component} from 'react';
import {Image, Upload, Button, message} from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import axios from 'axios';

class Avatar extends Component{
    constructor(props){
        super(props);
        this.state = {
            img: 'data:image/png;base64,',
            render: new FileReader(),
        }

        this.beforeUpload = this.beforeUpload.bind(this);
    }

    componentDidMount(){
        const that = this;
        axios({
            method: 'GET',
            url: 'api/user-information-management/avatar/download',
            headers: {
                "ps-token": window.localStorage.getItem("token")
            }
        }).then((res)=>{
            const img = res.data;
            if(img.length !== 0){that.setState({img: img});}
        })
    }

    beforeUpload(file){
        const isJpgOrPng = file.type === 'image/jpeg' || file.type === 'image/png';
        if(!isJpgOrPng){
            message.error("您只能上传图片文件!");
        }
        const isLt2M = file.size / 1024 / 1024 < 2;
        if (!isLt2M) {
            message.error('图片只能小于2MB!');
        }

        const reader = this.state.render;
        const that = this;
        reader.readAsDataURL(file);
        reader.onload=function(event){
            that.setState({img: event.target.result.replace("data:image/jpeg;", "data:image/png;")});
            axios({
                    method: 'POST',
                    url: 'api/user-information-management/avatar/upload',
                    headers: {
                        "ps-token": window.localStorage.getItem("token")
                    },
                    data: {
                        "avatarBase64": that.state.img
                    }
                }).then((res)=>{
                    console.log(res)
                })
        }
        return false;
    }

    render(){
        const beforeUpload = this.beforeUpload;
        const img = this.state.img;
        return(
            <div>
                <div>
                    <Image
                    width={200}
                    src={img}
                    />
                </div>
                <Upload beforeUpload={beforeUpload} maxCount={1}>
                    <Button icon={<UploadOutlined/>}>上传头像</Button>
                </Upload>
            </div>
        );
    }
}

export default Avatar;